CREATE VIEW lineitem AS SELECT j.l_orderkey AS l_orderkey, j.l_partkey AS l_partkey, j.l_suppkey AS l_suppkey, j.l_linenumber AS l_linenumber, j.l_quantity AS l_quantity, j.l_extendedprice AS l_extendedprice, j.l_discount AS l_discount, j.l_tax AS l_tax, j.l_returnflag AS l_returnflag, j.l_linestatus AS l_linestatus, j.l_shipdate AS l_shipdate, j.l_commitdate AS l_commitdate, j.l_receiptdate AS l_receiptdate, j.l_shipinstruct AS l_shipinstruct, j.l_shipmode AS l_shipmode, j.l_comment AS l_comment FROM (SELECT from_json(json, '{"l_orderkey": "INTEGER", "l_partkey": "INTEGER", "l_suppkey": "INTEGER", "l_linenumber": "INTEGER", "l_quantity": "INTEGER", "l_extendedprice": "DECIMAL(15,2)", "l_discount": "DECIMAL(15,2)", "l_tax": "DECIMAL(15,2)", "l_returnflag": "VARCHAR", "l_linestatus": "VARCHAR", "l_shipdate": "DATE", "l_commitdate": "DATE", "l_receiptdate": "DATE", "l_shipinstruct": "VARCHAR", "l_shipmode": "VARCHAR", "l_comment": "VARCHAR"}') AS j FROM lineitem_json);
CREATE VIEW orders AS SELECT j.o_orderkey AS o_orderkey, j.o_custkey AS o_custkey, j.o_orderstatus AS o_orderstatus, j.o_totalprice AS o_totalprice, j.o_orderdate AS o_orderdate, j.o_orderpriority AS o_orderpriority, j.o_clerk AS o_clerk, j.o_shippriority AS o_shippriority, j.o_comment AS o_comment FROM (SELECT from_json(json, '{"o_orderkey": "INTEGER", "o_custkey": "INTEGER", "o_orderstatus": "VARCHAR", "o_totalprice": "DECIMAL(15,2)", "o_orderdate": "DATE", "o_orderpriority": "VARCHAR", "o_clerk": "VARCHAR", "o_shippriority": "INTEGER", "o_comment": "VARCHAR"}') AS j FROM orders_json);
CREATE VIEW partsupp AS SELECT j.ps_partkey AS ps_partkey, j.ps_suppkey AS ps_suppkey, j.ps_availqty AS ps_availqty, j.ps_supplycost AS ps_supplycost, j.ps_comment AS ps_comment FROM (SELECT from_json(json, '{"ps_partkey": "INTEGER", "ps_suppkey": "INTEGER", "ps_availqty": "INTEGER", "ps_supplycost": "DECIMAL(15,2)", "ps_comment": "VARCHAR"}') AS j FROM partsupp_json);
CREATE VIEW part AS SELECT j.p_partkey AS p_partkey, j.p_name AS p_name, j.p_mfgr AS p_mfgr, j.p_brand AS p_brand, j.p_type AS p_type, j.p_size AS p_size, j.p_container AS p_container, j.p_retailprice AS p_retailprice, j.p_comment AS p_comment FROM (SELECT from_json(json, '{"p_partkey": "INTEGER", "p_name": "VARCHAR", "p_mfgr": "VARCHAR", "p_brand": "VARCHAR", "p_type": "VARCHAR", "p_size": "INTEGER", "p_container": "VARCHAR", "p_retailprice": "DECIMAL(15,2)", "p_comment": "VARCHAR"}') AS j FROM part_json);
CREATE VIEW customer AS SELECT j.c_custkey AS c_custkey, j.c_name AS c_name, j.c_address AS c_address, j.c_nationkey AS c_nationkey, j.c_phone AS c_phone, j.c_acctbal AS c_acctbal, j.c_mktsegment AS c_mktsegment, j.c_comment AS c_comment FROM (SELECT from_json(json, '{"c_custkey": "INTEGER", "c_name": "VARCHAR", "c_address": "VARCHAR", "c_nationkey": "INTEGER", "c_phone": "VARCHAR", "c_acctbal": "DECIMAL(15,2)", "c_mktsegment": "VARCHAR", "c_comment": "VARCHAR"}') AS j FROM customer_json);
CREATE VIEW supplier AS SELECT j.s_suppkey AS s_suppkey, j.s_name AS s_name, j.s_address AS s_address, j.s_nationkey AS s_nationkey, j.s_phone AS s_phone, j.s_acctbal AS s_acctbal, j.s_comment AS s_comment FROM (SELECT from_json(json, '{"s_suppkey": "INTEGER", "s_name": "VARCHAR", "s_address": "VARCHAR", "s_nationkey": "INTEGER", "s_phone": "VARCHAR", "s_acctbal": "DECIMAL(15,2)", "s_comment": "VARCHAR"}') AS j FROM supplier_json);
CREATE VIEW nation AS SELECT j.n_nationkey AS n_nationkey, j.n_name AS n_name, j.n_regionkey AS n_regionkey, j.n_comment AS n_comment FROM (SELECT from_json(json, '{"n_nationkey": "INTEGER", "n_name": "VARCHAR", "n_regionkey": "INTEGER", "n_comment": "VARCHAR"}') AS j FROM nation_json);
CREATE VIEW region AS SELECT j.r_regionkey AS r_regionkey, j.r_name AS r_name, j.r_comment AS r_comment FROM (SELECT from_json(json, '{"r_regionkey": "INTEGER", "r_name": "VARCHAR", "r_comment": "VARCHAR"}') AS j FROM region_json);